
public class Array1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int arr[]= {23,34,45,56,67};
		for(int i=0;i<arr.length;i++) {
			System.out.println(arr[i]);
		}
		System.out.println("\n");
		for(int i=arr.length-1;i>=0;i--) {
			System.out.println(arr[i]);

	}

}
}
