
public class pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1;i<=5;i++) // outer for loop - for rows
		{
			for (int j=1;j<=i;j++) // inner for loop
			{
				System.out.print("*");
			}
			System.out.println();

		}


	}

}
